import React, { useEffect, useState } from 'react';

const statusColors = {
  pending: '#f0ad4e',
  shipped: '#0275d8',
  delivered: '#5cb85c',
  cancelled: '#d9534f',
};

const Orders = ({ userEmail }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userEmail) {
      setError('User not logged in');
      setLoading(false);
      return;
    }
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/orders/' + encodeURIComponent(userEmail));
        if (!response.ok) {
          throw new Error('Failed to fetch orders');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [userEmail]);

  const requestCancellation = async (orderId) => {
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      // Update local state to mark cancellation requested
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, cancellationRequested: true } : order
        )
      );
      alert('Cancellation request sent successfully.');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  if (loading) {
    return <div className="page orders-page" style={{ padding: '2rem' }}>Loading orders...</div>;
  }

  if (error) {
    return <div className="page orders-page" style={{ padding: '2rem', color: 'red' }}>Error: {error}</div>;
  }

  if (orders.length === 0) {
    return <div className="page orders-page" style={{ padding: '2rem' }}>No orders found.</div>;
  }

  // Convert UTC ISO string to UTC+5:30 and format hours and minutes
  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return 'N/A';
    const date = new Date(dateTimeStr);
    // Convert to milliseconds and add 5.5 hours in ms
    const utcOffsetMs = 5.5 * 60 * 60 * 1000;
    const localDate = new Date(date.getTime() + utcOffsetMs);
    const options = {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'UTC',
    };
    return localDate.toLocaleString('en-GB', options);
  };

  return (
    <div className="page orders-page" style={{ maxWidth: '900px', margin: '2rem auto', padding: '0 1rem', paddingTop: '7rem' }}>
      <h1 style={{ fontSize: '2.5rem', fontWeight: '700', marginBottom: '2rem', color: '#2874f0', textAlign: 'center' }}>Your Orders</h1>
      {orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        return (
          <div
            key={index}
            className="order-card"
            style={{
              border: '1px solid #ddd',
              borderRadius: '12px',
              padding: '1.5rem',
              marginBottom: '1.5rem',
              backgroundColor: '#fff',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
              width: '100%',
              transition: 'box-shadow 0.3s ease',
            }}
            onMouseEnter={e => e.currentTarget.style.boxShadow = '0 8px 24px rgba(0,0,0,0.15)'}
            onMouseLeave={e => e.currentTarget.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)'}
          >
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h3 style={{ margin: 0, fontSize: '1.5rem', fontWeight: '700' }}>Order #{index + 1}</h3>
              <div style={{
                padding: '0.3rem 0.8rem',
                borderRadius: '20px',
                backgroundColor: statusColors[order.status.toLowerCase()] || '#777',
                color: '#fff',
                fontWeight: '700',
                textTransform: 'capitalize',
                fontSize: '0.9rem',
                userSelect: 'none',
              }}>
                {order.status}
              </div>
            </div>
            <p style={{ margin: '0.2rem 0' }}><strong>Order Date:</strong> {formatDateTime(order.order_date)}</p>
            <p style={{ margin: '0.2rem 0' }}><strong>City:</strong> {order.city}</p>
            <p style={{ margin: '0.2rem 0' }}><strong>Pincode:</strong> {order.pincode}</p>
            <p style={{ margin: '0.2rem 0', fontWeight: '700' }}><strong>Total Price:</strong> ₹{order.total_price}</p>
            <p style={{ margin: '0.2rem 0', fontWeight: '700' }}><strong>Total Items:</strong> {totalItems}</p>
            <div>
              <strong>Items:</strong>
              <ul style={{ listStyleType: 'none', paddingLeft: 0, marginTop: '0.5rem' }}>
                {order.items.map((item, idx) => (
                  <li key={idx} style={{ marginBottom: '0.75rem', display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    {item.image && (
                      <img
                        src={item.image}
                        alt={item.name}
                        style={{ width: '60px', height: '40px', objectFit: 'contain', borderRadius: '6px', border: '1px solid #ddd' }}
                      />
                    )}
                    <div>
                      <div style={{ fontWeight: '700', color: '#2874f0' }}>
                        {item.name} - Price: ₹{item.price}
                      </div>
                      <div>Quantity: {item.quantity}</div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
            <div style={{ marginTop: '1rem', display: 'flex', gap: '1rem' }}>
              <button
                style={{
                  backgroundColor: '#2874f0',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '700',
                  transition: 'background-color 0.3s ease',
                }}
                onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#1a5bb8')}
                onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#2874f0')}
                onClick={() => alert('Reorder feature coming soon!')}
              >
                Reorder
              </button>
              <button
                style={{
                  backgroundColor: '#6c757d',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '700',
                  transition: 'background-color 0.3s ease',
                }}
                onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#5a6268')}
                onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#6c757d')}
                onClick={() => alert('View Details feature coming soon!')}
              >
                View Details
              </button>
              {canRequestCancellation && (
                <button
                  style={{
                    backgroundColor: '#d9534f',
                    color: '#fff',
                    border: 'none',
                    padding: '0.5rem 1rem',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    fontWeight: '700',
                    transition: 'background-color 0.3s ease',
                  }}
                  onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#c9302c')}
                  onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#d9534f')}
                  onClick={() => requestCancellation(order._id)}
                >
                  Request Cancellation
                </button>
              )}
              {order.cancellationRequested && (
                <div style={{ color: '#d9534f', fontWeight: '700', alignSelf: 'center' }}>
                  Cancellation Requested
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Orders;
