import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const RegisterNew = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match");
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password,
        }),
      });
      const data = await response.json();
      if (response.ok) {
        alert('Registration successful! Please login.');
        navigate('/login');
      } else {
        setError(data.error || 'Registration failed');
      }
    } catch (error) {
      setError('Error: ' + error.message);
    }
  };

  return (
    <div style={{
      maxWidth: '400px',
      margin: '3rem auto',
      padding: '2rem',
      backgroundColor: '#1a1a2e',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
      color: '#e0e0e0',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <h2 style={{ color: '#00f0ff', marginBottom: '1.5rem', textAlign: 'center' }}>Create Your Account</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
        {error && <p style={{ color: '#ff4d6d', textAlign: 'center' }}>{error}</p>}
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '8px',
            border: '1px solid #00f0ff',
            background: '#16213e',
            color: '#e0e0e0',
            fontSize: '1rem'
          }}
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '8px',
            border: '1px solid #00f0ff',
            background: '#16213e',
            color: '#e0e0e0',
            fontSize: '1rem'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '8px',
            border: '1px solid #00f0ff',
            background: '#16213e',
            color: '#e0e0e0',
            fontSize: '1rem'
          }}
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirm Password"
          value={formData.confirmPassword}
          onChange={handleChange}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '8px',
            border: '1px solid #00f0ff',
            background: '#16213e',
            color: '#e0e0e0',
            fontSize: '1rem'
          }}
        />
        <button type="submit" style={{
          backgroundColor: '#00f0ff',
          color: '#1a1a2e',
          border: 'none',
          padding: '0.75rem',
          borderRadius: '8px',
          fontWeight: '700',
          fontSize: '1rem',
          cursor: 'pointer',
          transition: 'background-color 0.3s ease'
        }}
          onMouseEnter={e => e.currentTarget.style.backgroundColor = '#00c4cc'}
          onMouseLeave={e => e.currentTarget.style.backgroundColor = '#00f0ff'}
        >
          Register
        </button>
      </form>
      <p style={{ marginTop: '1rem', textAlign: 'center' }}>
        Already have an account? <Link to="/login" style={{ color: '#00f0ff', fontWeight: '600' }}>Login here</Link>.
      </p>
    </div>
  );
};

export default RegisterNew;
