import React, { useEffect, useState } from 'react';

const statusColors = {
  pending: '#f0ad4e',
  shipped: '#0275d8',
  delivered: '#5cb85c',
  cancelled: '#d9534f',
};

const Orders = ({ userEmail }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userEmail) {
      setError('User not logged in');
      setLoading(false);
      return;
    }
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/orders/' + encodeURIComponent(userEmail));
        if (!response.ok) {
          throw new Error('Failed to fetch orders');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [userEmail]);

  const requestCancellation = async (orderId) => {
    if (!orderId) {
      alert('Invalid order ID');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, cancellationRequested: true } : order
        )
      );
      alert('Cancellation request sent successfully.');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  if (loading) {
    return <div style={{ padding: '2rem', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>Loading orders...</div>;
  }

  if (error) {
    return <div style={{ padding: '2rem', color: 'red', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>Error: {error}</div>;
  }

  if (orders.length === 0) {
    return <div style={{ padding: '2rem', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>No orders found.</div>;
  }

  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return 'N/A';
    const date = new Date(dateTimeStr);
    const utcOffsetMs = 5.5 * 60 * 60 * 1000;
    const localDate = new Date(date.getTime() + utcOffsetMs);
    const options = {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'UTC',
    };
    return localDate.toLocaleString('en-GB', options);
  };

  return (
    <div style={{ maxWidth: '900px', margin: '4rem auto', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '1.5rem', color: '#007acc' }}>Your Orders</h1>
      {orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        const statusColor = statusColors[order.status.toLowerCase()] || '#f0ad4e';

        return (
          <div key={index} style={{
            border: '1px solid #ddd',
            borderRadius: '12px',
            padding: '1rem',
            marginBottom: '1.5rem',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            backgroundColor: '#fff'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h3 style={{ margin: 0 }}>Order #{index + 1}</h3>
              <div style={{
                backgroundColor: statusColor,
                color: 'white',
                padding: '0.25rem 0.75rem',
                borderRadius: '20px',
                fontWeight: '600',
                textTransform: 'capitalize',
                fontSize: '0.9rem'
              }}>
                {order.status}
              </div>
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <p><strong>Order Date:</strong> {formatDateTime(order.order_date)}</p>
              <p><strong>City:</strong> {order.city}</p>
              <p><strong>Pincode:</strong> {order.pincode}</p>
              <p><strong>Total Price:</strong> ₹{order.total_price}</p>
              <p><strong>Total Items:</strong> {totalItems}</p>
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Items:</strong>
              <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
                {order.items.map((item, idx) => (
                  <li key={idx} style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
                    {item.image && <img src={item.image} alt={item.name} style={{ width: '60px', height: '45px', objectFit: 'cover', borderRadius: '8px', marginRight: '1rem' }} />}
                    <div>
                      <div style={{ fontWeight: '600' }}>{item.name} - Price: ₹{item.price}</div>
                      <div>Quantity: {item.quantity}</div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
            <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
              <button
                onClick={() => alert('Reorder feature coming soon!')}
                style={{
                  backgroundColor: '#007acc',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  transition: 'background-color 0.3s ease'
                }}
                onMouseEnter={e => e.currentTarget.style.backgroundColor = '#005fa3'}
                onMouseLeave={e => e.currentTarget.style.backgroundColor = '#007acc'}
              >
                Reorder
              </button>
              {canRequestCancellation && (
                <button
                  onClick={() => requestCancellation(order._id)}
                  style={{
                    backgroundColor: '#d32f2f',
                    color: 'white',
                    border: 'none',
                    padding: '0.5rem 1rem',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    fontWeight: '600',
                    transition: 'background-color 0.3s ease'
                  }}
                  onMouseEnter={e => e.currentTarget.style.backgroundColor = '#b71c1c'}
                  onMouseLeave={e => e.currentTarget.style.backgroundColor = '#d32f2f'}
                >
                  Request Cancellation
                </button>
              )}
              {order.cancellationRequested && (
                <div style={{
                  color: '#d9534f',
                  fontWeight: '600',
                  alignSelf: 'center'
                }}>
                  Cancellation Requested
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Orders;
