import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!email || !password) {
      setError('Please enter email and password');
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        setError(data.error || 'Login failed');
      } else {
        localStorage.setItem('user', JSON.stringify({ email, name: data.name }));
        navigate('/home');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    }
  };

  return (
    <div className="product-card" style={{ maxWidth: '400px', margin: '3rem auto', padding: '2rem' }}>
      <h2 style={{ color: 'var(--neon-cyan)', marginBottom: '1.5rem', textAlign: 'center' }}>Login to ShopSphere</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
        {error && <p className="error" style={{ color: 'var(--accent-red)', textAlign: 'center' }}>{error}</p>}
        <label htmlFor="email" style={{ fontWeight: '600' }}>Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          required
          style={{ padding: '0.8rem', borderRadius: '8px', border: '1px solid var(--neon-cyan)', background: 'var(--card-bg)', color: 'var(--text-primary)' }}
        />
        <label htmlFor="password" style={{ fontWeight: '600' }}>Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter your password"
          required
          style={{ padding: '0.8rem', borderRadius: '8px', border: '1px solid var(--neon-cyan)', background: 'var(--card-bg)', color: 'var(--text-primary)' }}
        />
        <button type="submit" className="cyber-button" style={{ marginTop: '1rem' }}>Login</button>
      </form>
      <p style={{ marginTop: '1rem', textAlign: 'center' }}>
        New user? <Link to="/register" style={{ color: 'var(--neon-cyan)', fontWeight: '600' }}>Register now</Link>
      </p>
    </div>
  );
};

export default Login;
