import React, { useEffect, useState } from 'react';

const statusOptions = ['Pending', 'Shipped', 'Delivered', 'Cancelled'];

const styles = {
  container: {
    maxWidth: '1400px',
    margin: '2rem auto',
    padding: '2rem',
    fontFamily: "'Segoe UI', 'Roboto', sans-serif",
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.06)',
    minHeight: '80vh'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem',
    paddingBottom: '1.5rem',
    borderBottom: '1px solid #f0f0f0'
  },
  title: {
    fontSize: '1.8rem',
    fontWeight: '600',
    color: '#2d3436',
    margin: '0'
  },
  buttonGroup: {
    display: 'flex',
    gap: '1rem'
  },
  button: {
    padding: '0.75rem 1.5rem',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    fontWeight: '600',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem'
  },
  usersButton: {
    backgroundColor: '#3498db',
    color: 'white',
    ':hover': {
      backgroundColor: '#2980b9',
      transform: 'translateY(-1px)'
    }
  },
  logoutButton: {
    backgroundColor: '#ff4757',
    color: 'white',
    ':hover': {
      backgroundColor: '#ff6b81',
      transform: 'translateY(-1px)'
    }
  },
  statusIndicator: (status) => ({
    padding: '0.5rem 1rem',
    borderRadius: '20px',
    fontWeight: '600',
    textAlign: 'center',
    display: 'inline-flex',
    alignItems: 'center',
    gap: '0.5rem',
    ...(status === 'Delivered' && {
      backgroundColor: '#e3fcef',
      color: '#006644',
      border: '1px solid #a2f5bf'
    }),
    ...(status === 'Cancelled' && {
      backgroundColor: '#ffeef0',
      color: '#bf0711',
      border: '1px solid #ffd3d6'
    }),
    ...(status === 'Shipped' && {
      backgroundColor: '#e3f3ff',
      color: '#084b83',
      border: '1px solid #b3d4ff'
    }),
    ...(status === 'Pending' && {
      backgroundColor: '#fff8e6',
      color: '#8a6d3b',
      border: '1px solid #ffeeba'
    })
  }),
  tableContainer: {
    overflowX: 'auto',
    borderRadius: '10px',
