import React, { useState } from 'react';

const About = () => {
  const [feedback, setFeedback] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now, just simulate submission
    setSubmitted(true);
    setFeedback('');
  };

  return (
    <div style={{
      maxWidth: '800px',
      margin: '2rem auto',
      padding: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: '#333'
    }}>
      <h1 style={{ textAlign: 'center', color: '#2874f0', marginBottom: '1.5rem' }}>About Us</h1>
      <p style={{ fontSize: '1.1rem', lineHeight: '1.6' }}>
        This is a real-time e-commerce site built with React and Flask.
      </p>
      <h2 style={{ color: '#2874f0', marginTop: '2rem' }}>Our Mission</h2>
      <p style={{ fontSize: '1.1rem', lineHeight: '1.6' }}>
        Our mission is to provide customers with a seamless and enjoyable online shopping experience,
        offering a wide range of electronics and gadgets at competitive prices.
      </p>
      <h2 style={{ color: '#2874f0', marginTop: '2rem' }}>Features</h2>
      <ul style={{ fontSize: '1.1rem', lineHeight: '1.6', paddingLeft: '1.2rem' }}>
        <li>Real-time stock updates using WebSockets</li>
        <li>User authentication and registration</li>
        <li>Persistent shopping cart across sessions</li>
        <li>Order management and tracking</li>
        <li>Responsive and user-friendly interface</li>
      </ul>

      <h2 style={{ color: '#2874f0', marginTop: '2rem' }}>Feedback</h2>
      {submitted ? (
        <p style={{ fontSize: '1.1rem', color: 'green' }}>Thank you for your feedback!</p>
      ) : (
        <form onSubmit={handleSubmit} style={{ marginTop: '1rem' }}>
          <textarea
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Enter your feedback here"
            rows={5}
            style={{
              width: '100%',
              padding: '0.75rem',
              fontSize: '1rem',
              borderRadius: '8px',
              border: '1px solid #ccc',
              resize: 'vertical',
              fontFamily: 'inherit',
              color: '#333'
            }}
            required
          />
          <button
            type="submit"
            style={{
              marginTop: '1rem',
              backgroundColor: '#2874f0',
              color: '#fff',
              border: 'none',
              padding: '0.75rem 1.5rem',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '700',
              fontSize: '1rem',
              transition: 'background-color 0.3s ease',
              fontFamily: 'inherit'
            }}
            onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#1a5bb8')}
            onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#2874f0')}
          >
            Submit Feedback
          </button>
        </form>
      )}
    </div>
  );
};

export default About;
