import React, { useEffect, useState } from 'react';

const statusColors = {
  pending: '#f0ad4e',
  shipped: '#0275d8',
  delivered: '#5cb85c',
  cancelled: '#d9534f',
};

const Orders = ({ userEmail }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userEmail) {
      setError('User not logged in');
      setLoading(false);
      return;
    }
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/orders/' + encodeURIComponent(userEmail));
        if (!response.ok) {
          throw new Error('Failed to fetch orders');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [userEmail]);

  const requestCancellation = async (orderId) => {
    if (!orderId) {
      alert('Invalid order ID');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, cancellationRequested: true } : order
        )
      );
      alert('Cancellation request sent successfully.');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  if (loading) {
    return <div style={{
      maxWidth: '900px',
      margin: '4rem auto 3rem',
      padding: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: '#222',
      textAlign: 'center',
    }}>Loading orders...</div>;
  }

  if (error) {
    return <div style={{
      maxWidth: '900px',
      margin: '4rem auto 3rem',
      padding: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: '#d9534f',
      textAlign: 'center',
      fontWeight: '700',
    }}>Error: {error}</div>;
  }

  if (orders.length === 0) {
    return <div style={{
      maxWidth: '900px',
      margin: '4rem auto 3rem',
      padding: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: '#222',
      textAlign: 'center',
    }}>No orders found.</div>;
  }

  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return 'N/A';
    const date = new Date(dateTimeStr);
    const utcOffsetMs = 5.5 * 60 * 60 * 1000;
    const localDate = new Date(date.getTime() + utcOffsetMs);
    const options = {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'UTC',
    };
    return localDate.toLocaleString('en-GB', options);
  };

  return (
    <div style={{
      maxWidth: '900px',
      margin: '4rem auto 3rem',
      padding: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: '#222',
    }}>
      <h1 style={{
        fontSize: '2.4rem',
        fontWeight: '800',
        marginBottom: '2.5rem',
        color: '#222',
        textAlign: 'center',
        letterSpacing: '1.2px',
      }}>Your Orders</h1>
      {orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        const statusColor = statusColors[order.status.toLowerCase()] || '#f0ad4e';

        return (
          <div key={index} style={{
            backgroundColor: '#fff',
            borderRadius: '8px',
            boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
            padding: '1rem',
            marginBottom: '1.5rem',
            display: 'flex',
            gap: '1rem',
            alignItems: 'center',
            transition: 'box-shadow 0.3s ease',
          }}>
            <div style={{
              fontSize: '1.2rem',
              fontWeight: '600',
              marginBottom: '1rem',
              color: '#007acc',
              flex: '1 1 100%',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}>
              <h3 style={{ margin: 0 }}>Order #{index + 1}</h3>
              <div style={{
                backgroundColor: statusColor,
                color: '#fff',
                padding: '0.3rem 0.8rem',
                borderRadius: '12px',
                fontWeight: '700',
                fontSize: '0.9rem',
                textTransform: 'capitalize',
              }}>
                {order.status}
              </div>
            </div>
            <div style={{ flex: 2, fontSize: '1rem' }}>
              <p><strong>Order Date:</strong> {formatDateTime(order.order_date)}</p>
              <p><strong>City:</strong> {order.city}</p>
              <p><strong>Pincode:</strong> {order.pincode}</p>
              <p><strong>Total Price:</strong> ₹{order.total_price}</p>
              <p><strong>Total Items:</strong> {totalItems}</p>
            </div>
            <div style={{ flex: 3 }}>
              <strong>Items:</strong>
              <div style={{
                display: 'flex',
                flexWrap: 'wrap',
                gap: '1rem',
                marginTop: '0.5rem',
              }}>
                {order.items.map((item, idx) => (
                  <div key={idx} style={{
                    backgroundColor: '#fff',
                    borderRadius: '8px',
                    boxShadow: '0 1px 4px rgba(0,0,0,0.1)',
                    padding: '0.8rem',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    width: '45%',
                    transition: 'box-shadow 0.3s ease',
                  }}>
                    {item.image && <img src={item.image} alt={item.name} style={{
                      width: '100%',
                      height: '120px',
                      objectFit: 'cover',
                      borderRadius: '6px',
                      marginBottom: '0.5rem',
                    }} />}
                    <div style={{
                      fontWeight: '600',
                      fontSize: '1rem',
                      marginBottom: '0.3rem',
                      color: '#333',
                      textAlign: 'center',
                    }}>{item.name}</div>
                    <div>Price: ₹{item.price}</div>
                    <div style={{
                      fontSize: '0.9rem',
                      color: '#555',
                      textAlign: 'center',
                    }}>Quantity: {item.quantity}</div>
                  </div>
                ))}
              </div>
            </div>
            <div style={{
              marginTop: '1rem',
              display: 'flex',
              gap: '1rem',
              justifyContent: 'center',
              flex: '1 1 100%',
            }}>
              <button
                onClick={() => alert('Reorder feature coming soon!')}
                style={{
                  backgroundColor: '#007acc',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  fontSize: '1rem',
                  boxShadow: '0 2px 6px rgba(0,122,204,0.4)',
                  transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
                }}
                onMouseEnter={e => e.currentTarget.style.backgroundColor = '#005fa3'}
                onMouseLeave={e => e.currentTarget.style.backgroundColor = '#007acc'}
              >
                Reorder
              </button>
              {canRequestCancellation && (
                <button
                  onClick={() => requestCancellation(order._id)}
                  style={{
                    backgroundColor: '#d32f2f',
                    color: 'white',
                    border: 'none',
                    padding: '0.5rem 1rem',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    fontWeight: '600',
                    fontSize: '1rem',
                    boxShadow: '0 2px 6px rgba(211,47,47,0.4)',
                    transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
                  }}
                  onMouseEnter={e => e.currentTarget.style.backgroundColor = '#b71c1c'}
                  onMouseLeave={e => e.currentTarget.style.backgroundColor = '#d32f2f'}
                >
                  Request Cancellation
                </button>
              )}
              {order.cancellationRequested && (
                <div style={{
                  fontWeight: '700',
                  color: '#d9534f',
                  alignSelf: 'center',
                }}>
                  Cancellation Requested
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Orders;
