import React, { useEffect, useState } from 'react';
import '../styles/main.css';

const statusOptions = ['Pending', 'Shipped', 'Delivered', 'Cancelled'];

const AdminPanel = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5001/api/admin/orders');
      if (!response.ok) {
        throw new Error('Failed to fetch orders');
      }
      const data = await response.json();
      setOrders(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    setError('');
    setSuccessMsg('');
    try {
      const response = await fetch(`http://localhost:5001/api/admin/orders/${orderId}/status`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) {
        throw new Error('Failed to update order status');
      }
      setSuccessMsg('Order status updated successfully');
      // Update local state
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, status: newStatus, cancellationRequested: false } : order
        )
      );
    } catch (err) {
      setError(err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminLoggedIn');
    window.location.href = '/';
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'Delivered':
        return 'status-delivered';
      case 'Cancelled':
        return 'status-cancelled';
      case 'Shipped':
        return 'status-shipped';
      default:
        return 'status-pending';
    }
  };

  return (
    <div className="admin-panel-container">
      <div className="admin-panel-header">
        <h1>Admin Panel - Manage Orders</h1>
        <button className="admin-panel-logout-button" onClick={handleLogout}>Logout</button>
      </div>
      {loading && <p>Loading orders...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMsg && <p style={{ color: 'green' }}>{successMsg}</p>}
      {!loading && orders.length === 0 && <p>No orders found.</p>}
      {!loading && orders.length > 0 && (
        <table className="admin-panel-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>User Email</th>
              <th>Order Date</th>
              <th>Total Price</th>
              <th>Status</th>
              <th>Cancellation Requested</th>
              <th>Update Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id} onMouseEnter={e => e.currentTarget.style.backgroundColor = '#f5f5f5'} onMouseLeave={e => e.currentTarget.style.backgroundColor = '#fff'}>
                <td className="id-cell">{order._id}</td>
                <td>{order.user_email}</td>
                <td>{order.order_date ? new Date(order.order_date).toLocaleString() : 'N/A'}</td>
                <td>₹{order.total_price}</td>
                <td className={`status-cell ${getStatusClass(order.status)}`}>{order.status}</td>
                <td style={{ color: order.cancellationRequested ? '#d9534f' : 'green', fontWeight: '700' }}>
                  {order.cancellationRequested ? 'Yes' : 'No'}
                </td>
                <td>
                  <select
                    value={order.status}
                    onChange={(e) => handleStatusChange(order._id, e.target.value)}
                  >
                    {statusOptions.map((status) => (
                      <option key={status} value={status}>{status}</option>
                    ))}
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPanel;
