import React, { useEffect, useState } from 'react';

const statusColors = {
  pending: 'order-status pending',
  shipped: 'order-status shipped',
  delivered: 'order-status delivered',
  cancelled: 'order-status cancelled',
};

const Orders = ({ userEmail }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userEmail) {
      setError('User not logged in');
      setLoading(false);
      return;
    }
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/orders/' + encodeURIComponent(userEmail));
        if (!response.ok) {
          throw new Error('Failed to fetch orders');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [userEmail]);

  const requestCancellation = async (orderId) => {
    if (!orderId) {
      alert('Invalid order ID');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, cancellationRequested: true } : order
        )
      );
      alert('Cancellation request sent successfully.');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  if (loading) {
    return <div className="orders-container" style={{ textAlign: 'center' }}>Loading orders...</div>;
  }

  if (error) {
    return <div className="orders-container" style={{ textAlign: 'center', color: '#d9534f', fontWeight: '700' }}>Error: {error}</div>;
  }

  if (orders.length === 0) {
    return <div className="orders-container" style={{ textAlign: 'center' }}>No orders found.</div>;
  }

  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return 'N/A';
    const date = new Date(dateTimeStr);
    const utcOffsetMs = 5.5 * 60 * 60 * 1000;
    const localDate = new Date(date.getTime() + utcOffsetMs);
    const options = {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'UTC',
    };
    return localDate.toLocaleString('en-GB', options);
  };

  return (
    <main className="orders-container" aria-label="User Orders">
      <h1 className="orders-title">Your Orders</h1>
      {orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        const statusClass = statusColors[order.status.toLowerCase()] || 'order-status pending';

        return (
          <article key={order._id || index} className="order-card" aria-labelledby={`order-title-${index}`}>
            <header className="order-header" style={{ justifyContent: 'space-between', flexWrap: 'wrap', gap: '1rem' }}>
              <h2 id={`order-title-${index}`} style={{ flex: '1 1 auto', margin: 0 }}>Order #{index + 1}</h2>
              <span className={statusClass} aria-label={`Order status: ${order.status}`} style={{ flex: '0 0 auto' }}>
                {order.status}
              </span>
            </header>
            <section className="order-details" aria-label="Order details" style={{ marginBottom: '1rem' }}>
              <p><strong>Order Date:</strong> {formatDateTime(order.order_date)}</p>
              <p><strong>City:</strong> {order.city}</p>
              <p><strong>Pincode:</strong> {order.pincode}</p>
              <p><strong>Total Price:</strong> ₹{order.total_price}</p>
              <p><strong>Total Items:</strong> {totalItems}</p>
            </section>
            <section className="order-items" aria-label="Ordered items" style={{ marginBottom: '1rem' }}>
              {order.items.map((item, idx) => (
                <div key={idx} className="order-product-card" style={{ flex: '1 1 45%', minWidth: '200px' }}>
                  {item.image && <img src={item.image} alt={item.name} className="order-product-image" />}
                  <div className="order-product-info">
                    <h3 className="order-product-name">{item.name}</h3>
                    <p className="order-product-price">Price: ₹{item.price}</p>
                    <p className="order-product-quantity">Quantity: {item.quantity}</p>
                  </div>
                </div>
              ))}
            </section>
            <footer className="order-buttons" style={{ justifyContent: 'center', flexWrap: 'wrap', gap: '1rem' }}>
              <button
                onClick={() => alert('Reorder feature coming soon!')}
                className="order-button"
                aria-label={`Reorder order number ${index + 1}`}
              >
                Reorder
              </button>
              {canRequestCancellation && (
                <button
                  onClick={() => requestCancellation(order._id)}
                  className="order-button order-cancel-button"
                  aria-label={`Request cancellation for order number ${index + 1}`}
                >
                  Request Cancellation
                </button>
              )}
              {order.cancellationRequested && (
                <p className="order-cancellation-requested" aria-live="polite">
                  Cancellation Requested
                </p>
              )}
            </footer>
          </article>
        );
      })}
    </main>
  );
};

export default Orders;
