import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';

const Cart = () => {
  const { cartItems, removeFromCart, addToCart } = useContext(CartContext);
  const [address, setAddress] = useState('');
  const [orderPlaced, setOrderPlaced] = useState(false);

  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  const formatPrice = (price) => {
    return `₹${price}`;
  };

  const handleOrder = () => {
    if (!address) {
      alert('Please enter your address to place the order.');
      return;
    }
    // TODO: Implement order submission logic (e.g., API call)
    setOrderPlaced(true);
    clearCart(); // Clear cart after successful order
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <span className="cart-item-name">{item.name}</span>
              <span className="cart-item-quantity">Quantity: {item.quantity}</span>
              <span className="cart-item-price">{formatPrice(item.price * item.quantity)}</span>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </div>
          ))}
          <h3>Total: {formatPrice(totalPrice)}</h3>
          {!orderPlaced ? (
            <>
              <label htmlFor="address">Shipping Address:</label>
              <textarea
                id="address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter your shipping address"
                rows={4}
                style={{ width: '100%', marginBottom: '10px' }}
              />
              <button onClick={handleOrder}>Checkout</button>
            </>
          ) : (
            <p>Thank you for your order! Your products will be shipped soon.</p>
          )}
        </>
      )}
    </div>
  );
};

export default Cart;
