import React, { useEffect, useState } from 'react';

const statusColors = {
  pending: { color: '#8a6d3b', backgroundColor: '#fff3cd', border: '1px solid #ffeeba' },
  shipped: { color: '#0c5460', backgroundColor: '#d1ecf1', border: '1px solid #bee5eb' },
  delivered: { color: '#155724', backgroundColor: '#d4edda', border: '1px solid #c3e6cb' },
  cancelled: { color: '#721c24', backgroundColor: '#f8d7da', border: '1px solid #f5c6cb' },
  default: { color: '#333', backgroundColor: '#f5f5f5' }
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '2rem auto',
    padding: '1rem',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  title: {
    fontSize: '2rem',
    fontWeight: '700',
    color: '#333',
    marginBottom: '1.5rem',
    borderBottom: '2px solid #ddd',
    paddingBottom: '0.5rem',
  },
  orderCard: {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 1px 6px rgba(0,0,0,0.1)',
    marginBottom: '1.5rem',
    padding: '1rem',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '1rem',
  },
  status: (status) => ({
    ...statusColors[status.toLowerCase()] || statusColors.default,
    padding: '0.5rem 1rem',
    borderRadius: '20px',
    fontWeight: '600',
    textTransform: 'capitalize',
    display: 'inline-block',
  }),
  detailRow: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
    gap: '1rem',
    marginBottom: '1rem',
  },
  detailItem: {
    flex: '1 1 200px',
    backgroundColor: '#fefefe',
    padding: '0.75rem 1rem',
    borderRadius: '6px',
    boxShadow: 'inset 0 0 5px rgba(0,0,0,0.05)',
  },
  itemsContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '1rem',
  },
  productCard: {
    display: 'flex',
    gap: '1rem',
    backgroundColor: '#fafafa',
    borderRadius: '6px',
    padding: '0.75rem',
    flex: '1 1 300px',
    alignItems: 'center',
    boxShadow: '0 1px 4px rgba(0,0,0,0.05)',
  },
  productImage: {
    width: '80px',
    height: '80px',
    objectFit: 'cover',
    borderRadius: '6px',
    border: '1px solid #ddd',
  },
  productInfo: {
    flex: 1,
  },
  buttonGroup: {
    marginTop: '1rem',
    display: 'flex',
    gap: '1rem',
    flexWrap: 'wrap',
  },
  button: {
    padding: '0.5rem 1.25rem',
    borderRadius: '6px',
    border: 'none',
    cursor: 'pointer',
    fontWeight: '600',
    textTransform: 'uppercase',
    letterSpacing: '0.5px',
    transition: 'background-color 0.3s ease',
  },
  reorderButton: {
    backgroundColor: '#28a745',
    color: 'white',
  },
  cancelButton: {
    backgroundColor: '#dc3545',
    color: 'white',
  },
  cancellationNotice: {
    marginTop: '1rem',
    padding: '0.5rem 1rem',
    backgroundColor: '#f8d7da',
    color: '#721c24',
    borderRadius: '6px',
    fontWeight: '600',
  },
};

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    setLoading(true);
    setError('');
    try {
      const userEmail = localStorage.getItem('userEmail');
      if (!userEmail) {
        setError('User not logged in');
        setLoading(false);
        return;
      }
      const response = await fetch(`http://localhost:5001/api/orders/${encodeURIComponent(userEmail)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch orders');
      }
      const data = await response.json();
      setOrders(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const requestCancellation = async (orderId) => {
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      fetchOrders();
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return 'N/A';
    const date = new Date(dateTimeString);
    return date.toLocaleString();
  };

  return (
    <main style={styles.container} aria-label="User Orders">
      <h1 style={styles.title}>Order History</h1>
      {loading && <p>Loading orders...</p>}
      {error && <p style={{ color: '#dc3545', fontWeight: 'bold' }}>{error}</p>}
      {!loading && orders.length === 0 && <p>No orders found.</p>}
      {!loading && orders.length > 0 && orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        const statusStyle = styles.status(order.status.toLowerCase());
        const orderId = order._id || order.id;

        return (
          <article key={orderId || index} style={styles.orderCard}>
            <header style={styles.header}>
              <h2 style={{ margin: 0, fontSize: '1.5rem', color: '#1a1a1a', fontWeight: '600' }}>
                Order #{(order.orderNumber || index + 1).toString().padStart(4, '0')}
                <span style={{ fontSize: '0.9rem', color: '#6c757d', marginLeft: '0.75rem', fontWeight: '400' }}>
                  {order.city} • {order.pincode}
                </span>
              </h2>
              <div style={statusStyle}>
                {order.status === 'Delivered' && (
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-label="Delivered">
                    <path d="M20.285 2l-11.285 11.567-5.286-5.011-3.714 3.716 9 8.728 15-15.285z"/>
                  </svg>
                )}
                {order.status === 'Shipped' && (
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-label="Shipped">
                    <path d="M20 8h-3v-2c0-2.209-1.791-4-4-4h-2c-2.209 0-4 1.791-4 4v2h-3l-2 6h2v6h3v-6h4v6h3v-6h2l-2-6zm-8-5h2c1.104 0 2 .896 2 2v2h-4v-2c0-1.104.896-2 2-2z"/>
                  </svg>
                )}
                {order.status}
              </div>
            </header>

            <div style={styles.detailRow}>
              <div style={styles.detailItem}>
                <strong>ORDER DATE</strong>
                {formatDateTime(order.order_date)}
              </div>
              <div style={styles.detailItem}>
                <strong>TOTAL VALUE</strong>
                ₹{order.total_price?.toLocaleString('en-IN')}
              </div>
              <div style={styles.detailItem}>
                <strong>ITEMS ORDERED</strong>
                {totalItems} {totalItems > 1 ? 'Products' : 'Product'}
              </div>
              <div style={styles.detailItem}>
                <strong>PAYMENT METHOD</strong>
                {order.paymentMethod || 'N/A'}
              </div>
            </div>

            <div style={styles.itemsContainer}>
              {order.items.map((item, idx) => (
                <div key={idx} style={styles.productCard}>
                  {item.image && (
                    <img
                      src={item.image}
                      alt={item.name}
                      style={styles.productImage}
                    />
                  )}
                  <div style={styles.productInfo}>
                    <h3>{item.name}</h3>
                    <p>₹{item.price?.toLocaleString('en-IN')}</p>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                </div>
              ))}
            </div>

            <div style={styles.buttonGroup}>
              <button
                style={{ ...styles.button, ...styles.reorderButton }}
                onClick={() => alert('Reorder feature coming soon!')}
              >
                Reorder
              </button>
              {canRequestCancellation && (
                <button
                  style={{ ...styles.button, ...styles.cancelButton }}
                  onClick={() => orderId ? requestCancellation(orderId) : alert('Order ID missing')}
                >
                  Cancel Order
                </button>
              )}
              {order.cancellationRequested && (
                <p style={styles.cancellationNotice}>
                  Cancellation Request Submitted
                </p>
              )}
            </div>
          </article>
        );
      })}
    </main>
  );
};

export default Orders;
