import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/main.css';

const Cart = () => {
  const { cartItems, removeFromCart } = useContext(CartContext);
  const [city, setCity] = useState('');
  const [pincode, setPincode] = useState('');
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [orderMessage, setOrderMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [orderTotal, setOrderTotal] = useState(0);
  const navigate = useNavigate();

  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  const formatPrice = (price) => {
    return `₹${price}`;
  };

  const handleOrder = async () => {
    if (!city || !pincode) {
      alert('Please enter your city and pincode to place the order.');
      return;
    }
    const user = JSON.parse(localStorage.getItem('user'));
    const userEmail = user ? user.email : null;
    if (!userEmail) {
      alert('Please login to place an order.');
      return;
    }
    // Prepare order data
    const orderData = {
      user_email: userEmail,
      items: cartItems.map(item => ({
        id: item.id,
        name: item.name,
        quantity: item.quantity,
        price: item.price,
      })),
      city,
      pincode,
      total_price: totalPrice,
    };

    try {
      const response = await fetch('http://localhost:5001/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(orderData),
      });
      if (!response.ok) {
        throw new Error('Failed to place order');
      }
      const result = await response.json();
      setOrderTotal(totalPrice);
      cartItems.forEach(item => removeFromCart(item.id));
      setOrderPlaced(true);
      setOrderMessage('Thank you for your order! Your products will be shipped soon.');
      setShowPopup(true);
      // Navigate to orders page after order placement
      navigate('/orders');
    } catch (error) {
      alert(error.message);
    }
  };

  const closePopup = () => {
    setShowPopup(false);
    setOrderPlaced(false);
    setOrderMessage('');
    setCity('');
    setPincode('');
    setOrderTotal(0);
  };

  return (
    <div className="page cart-page">
      <h1>Your Cart</h1>
      {cartItems.length === 0 && !orderPlaced ? (
        <div className="cart-empty-message">
          <p>Your cart is empty.</p>
          <Link to="/">Go to Home Page</Link>
        </div>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <img
                  src={item.image || 'https://via.placeholder.com/120x90.png?text=Product+Image'}
                  alt={item.name}
                  className="cart-item-image"
                />
                <div className="cart-item-details">
                  <h3>{item.name}</h3>
                  <p>Quantity: {item.quantity}</p>
                  <p className="cart-item-price">
                    Price: {formatPrice(item.price * item.quantity)}
                  </p>
                </div>
                <button
                  onClick={() => removeFromCart(item.id)}
                  className="remove-button"
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
          {!orderPlaced && (
            <>
              <h3 className="cart-total">
                Total: {formatPrice(totalPrice)}
              </h3>
              <label htmlFor="city" className="cart-label">
                City:
              </label>
              <input
                type="text"
                id="city"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                placeholder="Enter your city"
                className="cart-input"
              />
              <label htmlFor="pincode" className="cart-label">
                Pincode:
              </label>
              <input
                type="text"
                id="pincode"
                value={pincode}
                onChange={(e) => setPincode(e.target.value)}
                placeholder="Enter your pincode"
                className="cart-input"
              />
              <button
                onClick={handleOrder}
                className="checkout-button"
              >
                Checkout
              </button>
            </>
          )}
        </>
      )}

      {showPopup && (
        <div className="order-popup">
          <div className="popup-content">
            <h2>Order Confirmed!</h2>
            <p>{orderMessage}</p>
            <p className="order-total">Total Paid: {formatPrice(orderTotal)}</p>
            <button
              onClick={closePopup}
              className="popup-close-button"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
