import React, { useState, useEffect, useContext } from 'react';
import ProductItem from './ProductItem';
import { CartContext } from '../context/CartContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    // TODO: Fetch products from backend API
    setProducts([
      { id: 1, name: 'Smartphone', price: 69999, category: 'Electronics', brand: 'Brand A' },
      { id: 2, name: 'Laptop', price: 89999, category: 'Electronics', brand: 'Brand B' },
      { id: 3, name: 'Running Shoes', price: 4999, category: 'Footwear', brand: 'Brand C' },
      { id: 4, name: 'Coffee Maker', price: 2999, category: 'Home Appliances', brand: 'Brand D' },
      { id: 5, name: 'Bluetooth Headphones', price: 7999, category: 'Electronics', brand: 'Brand E' },
      { id: 6, name: 'T-shirt', price: 999, category: 'Clothing', brand: 'Brand F' },
      { id: 7, name: 'Backpack', price: 60, category: 'Accessories', brand: 'Brand G' },
      { id: 8, name: 'Wrist Watch', price: 200, category: 'Accessories', brand: 'Brand H' },
      { id: 9, name: 'Smart TV', price: 999, category: 'Electronics', brand: 'Brand I' },
      { id: 10, name: 'Microwave Oven', price: 150, category: 'Home Appliances', brand: 'Brand J' },
      { id: 11, name: 'Jeans', price: 45, category: 'Clothing', brand: 'Brand K' },
      { id: 12, name: 'Sneakers', price: 90, category: 'Footwear', brand: 'Brand L' },
      { id: 13, name: 'Desk Lamp', price: 30, category: 'Home Decor', brand: 'Brand M' },
      { id: 14, name: 'Gaming Console', price: 399, category: 'Electronics', brand: 'Brand N' },
      { id: 15, name: 'Sunglasses', price: 75, category: 'Accessories', brand: 'Brand O' },
      { id: 16, name: 'Winter Jacket', price: 120, category: 'Clothing', brand: 'Brand P' },
      { id: 17, name: 'Electric Kettle', price: 40, category: 'Home Appliances', brand: 'Brand Q' },
      { id: 18, name: 'Tablet', price: 350, category: 'Electronics', brand: 'Brand R' },
      { id: 19, name: 'Office Chair', price: 180, category: 'Furniture', brand: 'Brand S' },
      { id: 20, name: 'Wireless Mouse', price: 25, category: 'Electronics', brand: 'Brand T' },
      { id: 21, name: 'Water Bottle', price: 15, category: 'Accessories', brand: 'Brand U' },
      { id: 22, name: 'Bluetooth Speaker', price: 60, category: 'Electronics', brand: 'Brand V' },
      { id: 23, name: 'Yoga Mat', price: 35, category: 'Fitness', brand: 'Brand W' },
      { id: 24, name: 'Hair Dryer', price: 50, category: 'Personal Care', brand: 'Brand X' },
      { id: 25, name: 'Digital Camera', price: 450, category: 'Electronics', brand: 'Brand Y' },
      { id: 26, name: 'Cookware Set', price: 100, category: 'Kitchen', brand: 'Brand Z' },
    ]);
  }, []);

  const formatPrice = (price) => {
    return `₹${price}`;
  };

  return (
    <div className="product-list">
      <h2>Products</h2>
      {products.map(product => (
        <ProductItem key={product.id} product={{...product, price: formatPrice(product.price)}} onAddToCart={() => addToCart(product)} />
      ))}
    </div>
  );
};

export default ProductList;
