import React, { useEffect, useState } from 'react';
import '../styles/main.css';

const statusColors = {
  pending: '#f0ad4e',
  shipped: '#0275d8',
  delivered: '#5cb85c',
  cancelled: '#d9534f',
};

const Orders = ({ userEmail }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userEmail) {
      setError('User not logged in');
      setLoading(false);
      return;
    }
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/orders/' + encodeURIComponent(userEmail));
        if (!response.ok) {
          throw new Error('Failed to fetch orders');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [userEmail]);

  const requestCancellation = async (orderId) => {
    if (!orderId) {
      alert('Invalid order ID');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/orders/${orderId}/request-cancellation`, {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to request cancellation');
      }
      // Update local state to mark cancellation requested
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === orderId ? { ...order, cancellationRequested: true } : order
        )
      );
      alert('Cancellation request sent successfully.');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  if (loading) {
    return <div className="page orders-page" style={{ padding: '2rem' }}>Loading orders...</div>;
  }

  if (error) {
    return <div className="page orders-page" style={{ padding: '2rem', color: 'red' }}>Error: {error}</div>;
  }

  if (orders.length === 0) {
    return <div className="page orders-page" style={{ padding: '2rem' }}>No orders found.</div>;
  }

  // Convert UTC ISO string to UTC+5:30 and format hours and minutes
  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return 'N/A';
    const date = new Date(dateTimeStr);
    // Convert to milliseconds and add 5.5 hours in ms
    const utcOffsetMs = 5.5 * 60 * 60 * 1000;
    const localDate = new Date(date.getTime() + utcOffsetMs);
    const options = {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'UTC',
    };
    return localDate.toLocaleString('en-GB', options);
  };

  return (
    <div className="orders-page-container">
      <h1 className="orders-page-title">Your Orders</h1>
      {orders.map((order, index) => {
        const totalItems = order.items.reduce((sum, item) => sum + item.quantity, 0);
        const canRequestCancellation = !order.cancellationRequested && order.status !== 'Cancelled' && order.status !== 'Delivered';
        const statusClass = {
          pending: 'status-pending-badge',
          shipped: 'status-shipped-badge',
          delivered: 'status-delivered-badge',
          cancelled: 'status-cancelled-badge',
        }[order.status.toLowerCase()] || 'status-pending-badge';
        return (
          <div key={index} className="order-card">
            <div className="order-header">
              <h3>Order #{index + 1}</h3>
              <div className={`order-status-badge ${statusClass}`}>
                {order.status}
              </div>
            </div>
            <div className="order-info">
              <p><strong>Order Date:</strong> {formatDateTime(order.order_date)}</p>
              <p><strong>City:</strong> {order.city}</p>
              <p><strong>Pincode:</strong> {order.pincode}</p>
              <p><strong>Total Price:</strong> ₹{order.total_price}</p>
              <p><strong>Total Items:</strong> {totalItems}</p>
            </div>
            <div>
              <strong>Items:</strong>
              <ul className="order-items-list">
                {order.items.map((item, idx) => (
                  <li key={idx} className="order-item">
                    {item.image && <img src={item.image} alt={item.name} />}
                    <div>
                      <div className="order-item-name-price">{item.name} - Price: ₹{item.price}</div>
                      <div>Quantity: {item.quantity}</div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
            <div className="order-actions">
              <button className="button-primary" onClick={() => alert('Reorder feature coming soon!')}>
                Reorder
              </button>
              {canRequestCancellation && (
                <button className="button-danger" onClick={() => requestCancellation(order._id)}>
                  Request Cancellation
                </button>
              )}
              {order.cancellationRequested && (
                <div className="cancellation-requested">Cancellation Requested</div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Orders;
