import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductList from '../components/ProductList';

const Home = () => {
  // Simulated user login state; replace with real auth logic later
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div>
      <h1>Welcome to the E-commerce Site</h1>
      {!isLoggedIn ? (
        <div>
          <p>Please <Link to="/register">Register</Link> if you are a new user, or <Link to="/login">Login</Link> if you already have an account.</p>
        </div>
      ) : (
        <ProductList />
      )}
    </div>
  );
};

export default Home;
