import React, { useEffect, useState } from 'react';

const styles = {
  container: {
    maxWidth: '1400px',
    margin: '2rem auto',
    padding: '2rem',
    fontFamily: "'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', sans-serif",
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
    minHeight: '80vh',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem',
    paddingBottom: '1.5rem',
    borderBottom: '1px solid #f0f0f0',
  },
  title: {
    fontSize: '1.8rem',
    fontWeight: '600',
    color: '#2d3436',
    margin: '0',
  },
  logoutButton: {
    backgroundColor: '#ff4757',
    color: 'white',
    border: 'none',
    padding: '0.75rem 1.5rem',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: '600',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    ':hover': {
      backgroundColor: '#ff6b81',
      transform: 'translateY(-1px)',
    },
  },
  tableContainer: {
    overflowX: 'auto',
    borderRadius: '10px',
    border: '1px solid #f0f0f0',
  },
  table: {
    width: '100%',
    borderCollapse: 'separate',
    borderSpacing: '0',
    minWidth: '800px',
  },
  th: {
    padding: '1rem',
    textAlign: 'left',
    backgroundColor: '#2d3436',
    color: 'white',
    fontWeight: '600',
    fontSize: '0.9rem',
    position: 'sticky',
    top: '0',
    ':first-child': {
      borderTopLeftRadius: '10px',
    },
    ':last-child': {
      borderTopRightRadius: '10px',
    },
  },
  td: {
    padding: '1.2rem 1rem',
    borderBottom: '1px solid #f0f0f0',
    fontSize: '0.95rem',
    color: '#2d3436',
  },
  row: {
    transition: 'all 0.2s ease',
    backgroundColor: 'white',
    ':hover': {
      backgroundColor: '#f8f9fa',
      transform: 'translateX(4px)',
    },
    ':nth-child(even)': {
      backgroundColor: '#fcfcfc',
    },
  },
  loadingText: {
    textAlign: 'center',
    padding: '2rem',
    color: '#636e72',
    fontSize: '1.1rem',
  },
  errorText: {
    backgroundColor: '#ffeef0',
    color: '#ff4757',
    padding: '1rem',
    borderRadius: '8px',
    margin: '1rem 0',
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    fontWeight: '500',
  },
  emptyState: {
    textAlign: 'center',
    padding: '3rem',
    color: '#636e72',
    fontSize: '1.1rem',
    backgroundColor: '#f8f9fa',
    borderRadius: '10px',
    margin: '2rem 0',
  },
};

const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [hoveredRow, setHoveredRow] = useState(null);

  useEffect(() => {
    const adminLoggedIn = localStorage.getItem('adminLoggedIn');
    if (!adminLoggedIn) {
      window.location.href = '/';
      return;
    }
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5001/api/admin/users');
      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }
      const data = await response.json();
      setUsers(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminLoggedIn');
    window.location.href = '/';
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>User Management Dashboard</h1>
        <button
          style={styles.logoutButton}
          onClick={handleLogout}
        >
          <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
            <polyline points="16 17 21 12 16 7"></polyline>
            <line x1="21" y1="12" x2="9" y2="12"></line>
          </svg>
          Logout
        </button>
      </div>

      {error && (
        <div style={styles.errorText}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="12" cy="12" r="10"></circle>
            <line x1="12" y1="8" x2="12" y2="12"></line>
            <line x1="12" y1="16" x2="12" y2="16"></line>
          </svg>
          {error}
        </div>
      )}

      {loading ? (
        <p style={styles.loadingText}>Loading user data...</p>
      ) : users.length === 0 ? (
        <div style={styles.emptyState}>No registered users found in the database</div>
      ) : (
        <div style={styles.tableContainer}>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={{ ...styles.th, width: '30%' }}>User ID</th>
                <th style={{ ...styles.th, width: '25%' }}>Name</th>
                <th style={{ ...styles.th, width: '45%' }}>Email</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr
                  key={user._id}
                  style={{
                    ...styles.row,
                    ...(hoveredRow === index && { backgroundColor: '#f5f6fa' }),
                  }}
                  onMouseEnter={() => setHoveredRow(index)}
                  onMouseLeave={() => setHoveredRow(null)}
                >
                  <td style={{ ...styles.td, fontFamily: 'monospace', color: '#57606f' }}>{user._id}</td>
                  <td style={styles.td}>{user.name}</td>
                  <td style={styles.td}>{user.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminUsers;
